@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Serif:wght@400;600&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  /* Custom design tokens */
  --font-sans: 'IBM Plex Sans', sans-serif;
  --font-serif: 'IBM Plex Serif', serif;
  --color-hero-gradient-start: var(--hero-gradient-start);
  --color-hero-gradient-end: var(--hero-gradient-end);
  --color-stat-amber: var(--stat-amber);
  --color-stat-teal: var(--stat-teal);
  --color-stat-violet: var(--stat-violet);
  --color-stat-rose: var(--stat-rose);
  --color-stat-sky: var(--stat-sky);
}

:root {
  --radius: 0.5rem;

  /* Core palette â€” deep slate blue base */
  --background: oklch(0.975 0.005 250);
  --foreground: oklch(0.18 0.025 250);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.025 250);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.025 250);

  /* Primary â€” deep slate indigo */
  --primary: oklch(0.35 0.12 260);
  --primary-foreground: oklch(0.98 0 0);

  --secondary: oklch(0.94 0.008 250);
  --secondary-foreground: oklch(0.3 0.05 260);
  --muted: oklch(0.94 0.006 250);
  --muted-foreground: oklch(0.52 0.02 250);
  --accent: oklch(0.93 0.015 250);
  --accent-foreground: oklch(0.3 0.05 260);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.91 0.01 250);
  --input: oklch(0.91 0.01 250);
  --ring: oklch(0.55 0.10 260);

  /* Charts */
  --chart-1: oklch(0.62 0.18 50);
  --chart-2: oklch(0.55 0.15 260);
  --chart-3: oklch(0.58 0.14 180);
  --chart-4: oklch(0.65 0.16 310);
  --chart-5: oklch(0.60 0.17 140);

  /* Sidebar â€” deep slate */
  --sidebar: oklch(0.22 0.04 260);
  --sidebar-foreground: oklch(0.92 0.01 250);
  --sidebar-primary: oklch(0.72 0.16 50);
  --sidebar-primary-foreground: oklch(0.15 0.02 50);
  --sidebar-accent: oklch(0.30 0.06 260);
  --sidebar-accent-foreground: oklch(0.92 0.01 250);
  --sidebar-border: oklch(0.30 0.04 260);
  --sidebar-ring: oklch(0.55 0.10 260);

  /* Custom tokens */
  --hero-gradient-start: oklch(0.35 0.12 260);
  --hero-gradient-end: oklch(0.28 0.10 285);
  --stat-amber: oklch(0.72 0.16 50);
  --stat-teal: oklch(0.62 0.14 180);
  --stat-violet: oklch(0.62 0.18 290);
  --stat-rose: oklch(0.65 0.18 15);
  --stat-sky: oklch(0.62 0.14 225);

  --gradient-hero: linear-gradient(135deg, oklch(0.35 0.12 260), oklch(0.28 0.10 285));
  --gradient-card-amber: linear-gradient(135deg, oklch(0.72 0.16 50 / 0.12), oklch(0.72 0.16 50 / 0.04));
  --gradient-card-teal: linear-gradient(135deg, oklch(0.62 0.14 180 / 0.12), oklch(0.62 0.14 180 / 0.04));
  --gradient-card-violet: linear-gradient(135deg, oklch(0.62 0.18 290 / 0.12), oklch(0.62 0.18 290 / 0.04));
  --gradient-card-rose: linear-gradient(135deg, oklch(0.65 0.18 15 / 0.12), oklch(0.65 0.18 15 / 0.04));
  --gradient-card-sky: linear-gradient(135deg, oklch(0.62 0.14 225 / 0.12), oklch(0.62 0.14 225 / 0.04));
  --shadow-card: 0 1px 3px oklch(0.18 0.025 250 / 0.06), 0 4px 12px oklch(0.18 0.025 250 / 0.04);
  --shadow-hero: 0 8px 32px oklch(0.28 0.10 285 / 0.25);
  --transition-smooth: all 0.2s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'IBM Plex Sans', sans-serif;
  }
}

@layer utilities {
  .font-serif {
    font-family: 'IBM Plex Serif', serif;
  }

  .hero-gradient {
    background: var(--gradient-hero);
    box-shadow: var(--shadow-hero);
  }

  .card-shadow {
    box-shadow: var(--shadow-card);
  }

  .stat-card-amber {
    background: var(--gradient-card-amber);
    border-color: oklch(0.72 0.16 50 / 0.2);
  }

  .stat-card-teal {
    background: var(--gradient-card-teal);
    border-color: oklch(0.62 0.14 180 / 0.2);
  }

  .stat-card-violet {
    background: var(--gradient-card-violet);
    border-color: oklch(0.62 0.18 290 / 0.2);
  }

  .stat-card-rose {
    background: var(--gradient-card-rose);
    border-color: oklch(0.65 0.18 15 / 0.2);
  }

  .stat-card-sky {
    background: var(--gradient-card-sky);
    border-color: oklch(0.62 0.14 225 / 0.2);
  }

  .icon-amber { color: oklch(0.60 0.18 50); }
  .icon-teal { color: oklch(0.50 0.14 180); }
  .icon-violet { color: oklch(0.52 0.18 290); }
  .icon-rose { color: oklch(0.55 0.18 15); }
  .icon-sky { color: oklch(0.50 0.14 225); }

  .badge-paid {
    background: oklch(0.58 0.14 160 / 0.12);
    color: oklch(0.40 0.14 160);
    border: 1px solid oklch(0.58 0.14 160 / 0.25);
  }

  .badge-unpaid {
    background: oklch(0.65 0.18 15 / 0.1);
    color: oklch(0.48 0.18 15);
    border: 1px solid oklch(0.65 0.18 15 / 0.25);
  }
}

